// MCP HTTP Server for Claude Code
// Full MCP protocol implementation

const body = $input.item.json.body;
const method = body?.method || 'unknown';

console.log('MCP HTTP Request:', JSON.stringify(body, null, 2));

// Handle MCP protocol methods
switch(method) {
  case 'initialize':
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          protocolVersion: '2024-11-05',
          capabilities: {
            tools: {},
            resources: {},
            prompts: {}
          },
          serverInfo: {
            name: 'n8n-mcp-http-server',
            version: '1.0.0'
          }
        }
      }
    };

  case 'tools/list':
    // Define your custom MCP tools here
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          tools: [
            {
              name: 'get_workflow_status',
              description: 'Get the status of n8n workflows',
              inputSchema: {
                type: 'object',
                properties: {
                  workflow_id: {
                    type: 'string',
                    description: 'The ID of the workflow'
                  }
                }
              }
            },
            {
              name: 'trigger_workflow',
              description: 'Trigger an n8n workflow',
              inputSchema: {
                type: 'object',
                properties: {
                  workflow_id: {
                    type: 'string',
                    description: 'The ID of the workflow to trigger'
                  },
                  data: {
                    type: 'object',
                    description: 'Data to pass to the workflow'
                  }
                },
                required: ['workflow_id']
              }
            }
          ]
        }
      }
    };

  case 'tools/call':
    const toolName = body.params?.name;
    const args = body.params?.arguments || {};

    // Implement tool logic
    switch(toolName) {
      case 'get_workflow_status':
        return {
          json: {
            jsonrpc: '2.0',
            id: body.id,
            result: {
              content: [
                {
                  type: 'text',
                  text: `Workflow status check for: ${args.workflow_id}\n\nThis is a placeholder. Implement actual n8n API calls here.`
                }
              ]
            }
          }
        };

      case 'trigger_workflow':
        return {
          json: {
            jsonrpc: '2.0',
            id: body.id,
            result: {
              content: [
                {
                  type: 'text',
                  text: `Triggered workflow: ${args.workflow_id}\n\nThis is a placeholder. Implement actual workflow triggering here.`
                }
              ]
            }
          }
        };

      default:
        return {
          json: {
            jsonrpc: '2.0',
            id: body.id,
            error: {
              code: -32602,
              message: `Unknown tool: ${toolName}`
            }
          }
        };
    }

  case 'resources/list':
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          resources: []
        }
      }
    };

  case 'prompts/list':
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          prompts: []
        }
      }
    };

  default:
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        error: {
          code: -32601,
          message: `Method not found: ${method}`
        }
      }
    };
}
