// MCP Tool Handler for Claude Code
// This processes MCP protocol requests from Claude Code

const body = $input.item.json.body;
const method = body?.method || 'unknown';

console.log('MCP Request:', JSON.stringify(body, null, 2));

// Handle different MCP methods
switch(method) {
  case 'initialize':
    // Return server capabilities
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          protocolVersion: '2024-11-05',
          capabilities: {
            tools: {},
            resources: {}
          },
          serverInfo: {
            name: 'n8n-mcp-server',
            version: '1.0.0'
          }
        }
      }
    };

  case 'tools/list':
    // Return available tools
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          tools: [
            {
              name: 'example_tool',
              description: 'An example MCP tool',
              inputSchema: {
                type: 'object',
                properties: {
                  message: {
                    type: 'string',
                    description: 'A message to process'
                  }
                },
                required: ['message']
              }
            }
          ]
        }
      }
    };

  case 'tools/call':
    // Execute a tool
    const toolName = body.params?.name;
    const args = body.params?.arguments || {};

    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        result: {
          content: [
            {
              type: 'text',
              text: `Tool '${toolName}' executed with args: ${JSON.stringify(args)}`
            }
          ]
        }
      }
    };

  default:
    // Unknown method
    return {
      json: {
        jsonrpc: '2.0',
        id: body.id,
        error: {
          code: -32601,
          message: `Method not found: ${method}`
        }
      }
    };
}
